package org.eobjects.analyzer.cluster;

import org.eobjects.metamodel.util.HasName;

/**
 * Represents a node in a cluster. Each node must at minimum have a name to
 * identify themselves. A particular {@link ClusterManager} may add more
 * information to the node, e.g. to be able to send work to the nodes.
 */
public interface ClusterNode extends HasName {

}
